static inline int M_Sin(int angle) {
    while(angle < 0) angle += 360;
    while(angle >= 360) angle -= 360;
    return sin_table[angle];
}

static inline int M_Cos(int angle) {
    while(angle < 0) angle += 360;
    while(angle >= 360) angle -= 360;
    return cos_table[angle];
}